create or replace view sch_report_sepa.account_organisation as select acc_org.* from account_organisation acc_org join account acc on acc.id=acc_org.id where acc.competent_authority = 'SCOTLAND';

create or replace view sch_report_opred.account_organisation as select acc_org.* from account_organisation acc_org join account acc on acc.id=acc_org.id where acc.competent_authority = 'OPRED';

create or replace view sch_report_nrw.account_organisation as select acc_org.* from account_organisation acc_org join account acc on acc.id=acc_org.id where acc.competent_authority = 'WALES';

create or replace view sch_report_niea.account_organisation as select acc_org.* from account_organisation acc_org join account acc on acc.id=acc_org.id where acc.competent_authority = 'NORTHERN_IRELAND';

create or replace view sch_report_ea.account_organisation as select acc_org.* from account_organisation acc_org join account acc on acc.id=acc_org.id;



create or replace view sch_report_sepa.account_organisation_onboarding_registry as select aoor.* from account_organisation_onboarding_registry aoor where aoor.competent_authority = 'SCOTLAND';

create or replace view sch_report_opred.account_organisation_onboarding_registry as select aoor.* from account_organisation_onboarding_registry aoor where aoor.competent_authority = 'OPRED';

create or replace view sch_report_nrw.account_organisation_onboarding_registry as select aoor.* from account_organisation_onboarding_registry aoor where aoor.competent_authority = 'WALES';

create or replace view sch_report_niea.account_organisation_onboarding_registry as select aoor.* from account_organisation_onboarding_registry aoor where aoor.competent_authority = 'NORTHERN_IRELAND';

create or replace view sch_report_ea.account_organisation_onboarding_registry as select aoor.* from account_organisation_onboarding_registry aoor;


create or replace view sch_report_ea.noc as select n.* from noc n where exists (select null from account a where a.id = n.account_id);
create or replace view sch_report_sepa.noc as select n.* from noc n where exists (select null from account a where a.id = n.account_id and a.competent_authority = 'SCOTLAND');
